import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import { makeStyles, Card, Fab, CardMedia, CardContent, Box, Grid, Container, CardActionArea, CardActions } from '@material-ui/core';
import CreatePostForm from "./CreatePostForm"


const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    img: {
    },
    margin: {
    margin: theme.spacing(1),
    },
    card: {
      width: "100%",
    },
    input: {
      margin: 0,

    }

}));

export default function CreatePost() {
  const classes = useStyles();
  const [previewImages, setPreviewImages] = useState([]);
  const [images, setImages] = useState([])
  
  const handleChange =  e => {
        Array.from(e.target.files).forEach(file => { 
        const reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onloadend = () => {
          if (reader.readyState === 2) {
            setImages(prevImages => prevImages.concat(file))
            setPreviewImages(prevImages => prevImages.concat(reader.result))
          }
        }
      })
      console.log(images);
    }
  
  const cardCustom = previewImages.map(image => {
    return (
      <Grid key={previewImages.indexOf(image)} item xs={3}>
        <CardMedia
          component="img"
          alt="image"
          height="140vh"
          className={classes.img}
          image={image}
        />
        </Grid>
    )
  })
  const cardTest = arrayOfImages.map(image => {
    return (
      <Grid key={previewImages.indexOf(image)} item xs={3}>
        <CardMedia
          component="img"
          alt="image"
          height="140vh"
          className={classes.img}
          image={image}
        />
      </Grid>
    )
  })

 
  return (
    <Container >
      <Grid container justify="center" spacing={2}>
        {cardCustom}
        {cardTest}
          {/* {images.length > 0 &&  */}
        <Grid item xs={6}>
          <Card className={classes.card}>
            <CardContent className={classes.card}>
              <CreatePostForm className={classes.card} images={previewImages}/>
            </CardContent>
          </Card>
        </Grid>
        {/* } */}
      </Grid>

      <Box display="flex" justifyContent="flex-end" m={2} p={1}>
          <label htmlFor="upload-image-button"> 
            <Fab color="secondary" aria-label="add" component="span" className={classes.margin}>
              <AddIcon />
            </Fab>      
          </label>
      </Box>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
    </Container>
  );
}
