import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Axios from "axios"
import Avatar from "@material-ui/core/Avatar";
import AddIcon from '@material-ui/icons/Add';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
    root: {
    },
    avatarHover: {
        backgroundColor: "rgba(0, 0, 0, 0.2);",
        display: "flex",
        width:"100%",
        height: "100%",
        alignItems: "center",
    },


}));

export default function UserAvatar(props) {
    const [isShown, setIsShown] = useState(false);
    const classes = useStyles();
    const [profileAvatar, setProfileAvatar] = useState()
    const {username, _id} = props.user

    const handleAvatarUpload = async e => {
        e.preventDefault();
        const imageFormData = new FormData(document.getElementById("uploadAvatarImage"));

        // formData.append(date, username)
        for (const p of imageFormData) {
            console.log(p);
        }
        await Axios.post(`http://localhost:3000/api/upload-image/${username}/${_id}`)
            .then(
                await Axios({
                    method: "POST",
                    url: `http://localhost:3000/api/upload-image/${_id}`,
                    withCredentials: "same-origin",
                    data: imageFormData,
                    headers: {
                        "Accept": "multipart/form-data",
                        "Content-Type": "application/x-www-form-urlencoded",
                    },
                })
                    .then(function (response) {
                        console.log(response.data);
                    })
            )
    }
 
    Axios.get(`http://localhost:3000/api/get-images/avatar/${_id}`)
    .then(function (response) {
              console.log(response.data);
          })



    return (
        <form id="uploadAvatarImage">
            <Avatar 
            variant="rounded"
            onClick={handleAvatarUpload}
            >
                <div 
                // className={classes.avatarHover}
                onMouseEnter={() => setIsShown(true)}
                onMouseLeave={() => setIsShown(false)}
                >
                    <Fade in={isShown}><div className={classes.avatarHover}></div></Fade>
                    {isShown && (
                        <AddIcon  style={{ width:"100%"}} />
                    )}
                </div>
            </Avatar>
        </form>
    )

}
