import React, { useState, useContext } from "react";
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";

import { InputLabel, InputAdornment, Input, Button, Grid, Link } from '@material-ui/core';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import LabelIcon from '@material-ui/icons/Label';



export default function CreatePostForm(props) {
    const [tags, setTags] = useState([]);
    const user = useContext(UserContext)
    const api = useContext(ApiContext)
    const { username, _id } = user.data



    const handleUpload = async e => {


        const post = {
            creator: username,
            creatorId: _id,
            image: props.images,
            created: new Date().getDay(),
            // price: price,
            // tags: tags
        }

        api.createPost(post)
    }
    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            setTags(...tags, e.target.value)
                }
    }
    return (
        <div>
            <Grid item >
                <InputLabel style={{ fontSize: 13 }}>Choose a price</InputLabel>
                <Input
                    id="Price"
                    style={{ width: "100%" }}
                    placeholder="5$"
                    type="number"
                    onChange={(e) => setTags( ...tags, e.target.value )}
                    startAdornment={
                        <InputAdornment position="start">
                            <MonetizationOnIcon />
                        </InputAdornment>
                    }
                   
                />
            </Grid>
            <br />
            <Grid item>
                <InputLabel style={{ fontSize: 13 }}>Tags</InputLabel>
                <Input
                    style={{ width: "100%" }}
                    id="Tags"
                    placeholder="Red dress..."
                    
                    startAdornment={
                        <InputAdornment position="start">
                            <LabelIcon />
                        </InputAdornment>
                    }
                    endAdornment={
                        <Button onClick={(e) => setTags(prevTags => prevTags.push("#" + e.target.value))} onKeyDown={handleKeyPress}>
                            ADD
                        </Button>
                    }
                />
            </Grid>
            <Link>Choose</Link>
            <Grid item>
                    {tags && tags.map(tag => 
                        <Link href={tag} onClick={(e) => e.preventDefault()} variant="body2">
                            {tag}
                        </Link>
                    )}
            </Grid>
            <br />
            <Grid item>
                <Button
                    variant="contained"
                    color="default"
                    fullWidth="true"
                    startIcon={<CloudUploadIcon />}
                    onClick={handleUpload}
                >
                    Upload
                </Button>
            </Grid>
        </div>
    )
}
