import React, { useEffect, useState, useContext } from "react"; 
import CssBaseline from '@material-ui/core/CssBaseline';
import Header from "./components/Header"
import Footer from "./components/Footer"
import Dashboard from "./components/Dashboard/Dashboard"
import Login from "./components/Login"
import Signup from "./components/Signup"
import Home from "./components/Home"
import ProtectedRoute from "./components/ProtectedRoute"
import { Route, Switch } from "react-router-dom";
import Theme from "./components/Theme"
import {ThemeProvider} from '@material-ui/core/styles';
import Spinner from "./components/spinner" 
import { ApiContext } from "./context/ApiContext";
import { UserContext } from "./context/UserContext";
// import * as api from "./API/index"
// import User from "./components/getUser"
// import * as user from "./API/index"
// import Axios from "axios";



function  App() {
  const [user, setUser] = useState()
  const api = useContext(ApiContext)

  useEffect(() => {
    api.getUser().then(response => console.log(response))
  // User((res) => {setUser(res)}) 
    }, [])

  if (user === null) {
    return (
      <Spinner/>
    )
  }


  return (
    <div className="App">
    {/* in order to use a personalized materlial-ui theme we need to wrap the app in a ThemeProvider */}
      {/* <ApiContext.Provider value={api}> */}
        <UserContext.Provider value={user}>
        <ThemeProvider theme={Theme}>
          <CssBaseline />
            {/* <Header user={user}/> */}
            <Header />
              <Switch>
                <Route exact from="/" component={Home} />
                <Route exact from="/Home" component={Home} />
                {/* <ProtectedRoute path="/dashboard" user={user} component={Dashboard}/> */}
                <ProtectedRoute path="/dashboard"  component={Dashboard}/> 
                 <Route exact path="/signup"  component={Signup} /> 
                  {/* <Route exact path="/signup" user={user} component={Signup} /> */}
                  <Route exact path="/login"  component={Login} />
                  {/* <Route exact path="/login" user={user} component={Login} /> */}
                {/*   */}
                <Route exact path="/logout" />
                <Route path="*" component={() => "404 NOT FOUND"} />
              </Switch>
            <Footer/>
        </ThemeProvider>
        </UserContext.Provider>
      {/* </ApiContext.Provider> */}
    </div>
  );
}

export default App;
