import React, { useState, useContext } from "react";
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import AddIcon from '@material-ui/icons/Add';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";
import { InputLabel, InputAdornment, Input, makeStyles, Card, FormControl, CardActionArea, CardActions, Fab, CardMedia, CardContent, Button, Box, Grid, Container, } from '@material-ui/core';
// import FileBase from 'react-file-base64'
// import Axios from "axios"

const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    img: {
      borderRadius: "8px",
      maxWidth: "140vh",
      cursor: "pointer",
    },
    margin: {
    margin: theme.spacing(2),
    },
    filebase: {
      display: "none"
    },
    iconButton: {
      backgroundColor: "#074A49",
      color: "white",
      margin: theme.spacing(2),

    }

}));

export default function CreatePost(props) {
  // const [image, setImage] = useState({ preview0: "", raw0: "", });
  const [images, setImages] = useState([]);
  // preview1: "", raw1: "", preview2: "", raw2: "", preview3: "", raw3: "",
  const user = useContext(UserContext)
  const api = useContext(ApiContext)
  const classes = useStyles();
  const arrayOfImages = []
  class Image { constructor(base64Data){ this.base64Data = base64Data }
 }
  const handleChange = async e => {
       await Array.from(e.target.files).forEach(file => { 
        const reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onloadend = () => {
          if (reader.readyState === 2) {
            const image = new Image(reader.result)
            arrayOfImages.push(image)
            setImages(prevImages => prevImages.concat(reader.result))
            return arrayOfImages
          }
        }
      })
    }
    // handleUpload();
  const {username, _id} = user.data

  const handleUpload = async e => {


    const post = {
      creator: username,
      creatorId: _id,
      // image: image,
      created: new Date().getDay(),
      // price: price,
      // tags: tags
    }
    
    api.createPost(post)
  }

  const cardCustom = images.map(image => {
    return (
        <Grid item xs={10} sm={3}>
        <Card className={classes.root}>
          <CardActions>
            <CardActionArea>
              <CardMedia
                component="img"
                alt="image"
                height="180vh"
                image={image}
              />
            </CardActionArea>
          </CardActions>
        </Card>
        </Grid>

    )
  })

 
  return (
    <Box display="flex" maxWidth="100%" className={classes.root} >
      <Container >
        {/* <FormControl id="uploadImage" > */}
            <Grid container justify="center" spacing={3} xs={12} >
                {cardCustom}
          </Grid>
          <CardContent>
            <Grid justify="center">
            {images.length > 1 && 
                <FormControl className={classes.root} noValidate autoComplete="off">
              <Grid item flexGrow={1}  >
              <InputLabel htmlFor="Price" display="block">Price</InputLabel>
              <Input
                id="Price"
                startAdornment={
                  <InputAdornment position="start">
                    <MonetizationOnIcon />
                  </InputAdornment>
                }
              />
              </Grid>
              <Grid item flexGrow={1}>
                {/* <Button size="small" style={{ alignItems: "center" }} onClick={handleUpload}> Upload </Button> */}
                <Button
                  variant="contained"
                  color="default"
                  fullWidth="true"
                  className={classes.button}
                  startIcon={<CloudUploadIcon />}
                  onClick={handleUpload}
                >
                  Upload
                </Button>
              </Grid>
              </FormControl>}
              </Grid>
            </CardContent>

          <Box display="flex" justifyContent="flex-end" m={2} p={1}>
              <label htmlFor="upload-image-button"> 
              <Fab color="secondary" aria-label="add" component="span" className={classes.margin}>
                <AddIcon />
              </Fab>      
              </label>
          </Box>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
      <br />
      {/* </FormControl> */}
      </Container>
    </Box>
  );
}
