import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import ShareIcon from '@material-ui/icons/Share';
import EditProfileForm from "./EditProfileForm"
import uploadImage from "./uplaodImage"

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        borderRadius: 0,
        // backgroundColor: "#F5EB00"
    },
    icons: {
        color: "#000",
        fontSize: "2vh",
    },
    title:{
        fontFamily: "Abril Fatface",
    },

}));

export default function Dashboard(props) {
    const classes = useStyles();
    const {socialMediaAccounts, username, webpage, email} = props.user

    const accounts = []

        for (const [index, value] of Object.entries(socialMediaAccounts)) {
            accounts.push(
                <Typography variant="body2">
                <i class={value.account && value.icon}>&nbsp;</i>
                <strong>{value.account && index}&nbsp;</strong>
                {value.account}&nbsp;</Typography>
            )
        }
    const [image, setImage] = useState({ preview: "", raw: "" });

    const handleChange = e => {
        if (e.target.files.length) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload = async e => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("image", image.raw);

        await fetch("YOUR_URL", {
            method: "POST",
            headers: {
                "Content-Type": "multipart/form-data"
            },
            body: formData
        });
    };

    return (
        <div>

            {/* ----------------------------------   Header  ----------------------------------- */}

        <Card variant="outlined" className={classes.root}>
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardHeader
                        avatar={
                            <Avatar variant="rounded" ></Avatar>
                        }
                        title={<Typography className={classes.title} color="textPrimary" >
                        <h1>|{username}</h1>
                        </Typography>}
                            subheader={<p><i class="fas fa-at"></i> {email} <i class="far fa-address-card"></i> {webpage}</p>}
                        />
                </Box>
                    <Box mx={4} my={6}>
                        <EditProfileForm user={props.user} alert={alert} />
                    </Box>
            </Box>
        <Box style={{ width: '100%' }} >
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardActions style={{width: "100%"}} >
                        {accounts}
                    </CardActions> 
                </Box>
                        <Box mx={4}><ShareIcon /></Box>
            </Box>
        </Box>
        </Card>
            {/* ----------------------------------   Photos Section  ----------------------------------- */}
            <div>
                <label htmlFor="upload-button">
                    {image.preview ? (
                        <img src={image.preview} alt="dummy" width="300" height="300" />
                    ) : (
                            <>
                                <span className="fa-stack fa-2x mt-3 mb-2">
                                    <i className="fas fa-circle fa-stack-2x" />
                                    <i className="fas fa-store fa-stack-1x fa-inverse" />
                                </span>
                                <button className="text-center">Upload your photo</button>
                            </>
                        )}
                </label>
                <input
                    type="file"
                    id="upload-button"
                    style={{ display: "none" }}
                    onChange={handleChange}
                />
            <Button color="secondary" onClick={handleUpload} variant="outlined"> Upload img </Button>
                {/* <Button onClick={handleUpload}>Upload</Button> */}
            </div>
            <uploadImage></uploadImage>
        </div>
    );
}
