import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { UserContext } from "../../context/UserContext"
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
// import getUser from "../getUser"
import FormHelperText from '@material-ui/core/FormHelperText';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Axios from "axios";
import { ApiContext } from '../../context/ApiContext';


const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: 0,
        // boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

export default function EditProfileForm() {
    const user = useContext(UserContext)
    const api = useContext(ApiContext)

    const { username, email, webpage, socialMediaAccounts, _id} = user.user
    
    const classes = useStyles();

    const [newPassword, setPassword] = useState();
    const [confirmPassword, setConfirmPassword] = useState()
    const [passwordTag, setPasswordTag] = useState();
    const [open, setOpen] = useState();


    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    function handlePasswordOnSubmit(){
        confirmPassword !== newPassword ? setPasswordTag("error") : updateProfile() 
    }
    
    const updateProfile = async () => {

        let editFormData = new FormData(document.getElementById("form"));
        function convertFD2JSON(formData) {
            let obj = {};
            for (let key of formData.keys()) {
                obj[key] = formData.get(key);
            }
            return JSON.stringify(obj);
        }
        let jsonData = convertFD2JSON(editFormData);
        console.log(jsonData);
        api.editProfile(editFormData)
            .then(function (response) {
                console.log(response.data);
                handleClose()
            })
    }
// Nota Mirar el css de Modal
    return (
        <div>
            <Button type="button" variant="outlined" onClick={handleOpen}>
                Edit Profile
      </Button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 300,
                }}
            >
                <Fade in={open} >
                    <form id="form">
                    <div className={classes.paper} >
                        <Typography variant="h5" mt={1}>Edit Your Profile</Typography>
                        <Divider/>
                            <Box display="flex" >
                                <Box  m={1}  >
                                    <Typography variant="body1" flexGrow={1}>Username</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={username}
                                name="username"
                                id="Username" 
                                />
                                </Box>
                                <Box  m={1}  >
                                    <Typography variant="body1" flexGrow={1}>Email</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={email} 
                                name="email"
                                id="email"
                                />
                                </Box>
                            </Box>            
                        <Typography variant="h5">Contact details</Typography>
                        <Divider />
                            <Box display="flex" justifyContent="felex-start" flexWrap="wrap">
                            <Box m={1} >
                                <Typography variant="body1">Web Page</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={webpage}
                                name="webpage"
                                id="webPage" 
                                />
                            </Box>
                            <Box m={1} >
                                <Typography variant="body1">Instagram</Typography>
                                <TextField 
                                defaultValue={socialMediaAccounts.instagram && socialMediaAccounts.instagram.account} 
                                variant="outlined" id="Instagram"
                                name="instagram"
                                />
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Facebook</Typography>
                                <TextField 
                                variant="outlined"
                                name="facebook"
                                defaultValue={socialMediaAccounts.facebook && socialMediaAccounts.facebook.account } 
                                id="Facebook" 
                                />
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Twitter</Typography>
                                <TextField 
                                variant="outlined"
                                name="twitter"
                                defaultValue={socialMediaAccounts.twitter && socialMediaAccounts.twitter.account} 
                                id="Twitter" 
                                />
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Snapchat</Typography>
                                <TextField variant="outlined" 
                                defaultValue={socialMediaAccounts.snapchat && socialMediaAccounts.snapchat.account}
                                name="snapchat" 
                                id="Snapchat" 
                                />
                            </Box>
                            <Box  m={1} >
                                    <Typography variant="body1">Flickr</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={socialMediaAccounts.flickr && socialMediaAccounts.flickr.account }
                                name="flickr"
                                id="Flickr" 
                                />
                            </Box>
                        </Box> 
                    <Typography variant="h5">Change your Password</Typography>
                    <Divider />
                        <Box display="flex">
                            <Box  m={1}  >
                                <Typography variant="body1"  flexGrow={1}>Password</Typography>
                                    <OutlinedInput 
                                        error={ passwordTag }
                                        type="password"
                                        name="password"
                                        variant="outlined"
                                        id="confirmPassword"
                                        onChange={(event) => setPassword(event.target.value)} />
                                    <FormHelperText>{passwordTag && <p style={{ color: "red" }}>Passwords don not match</p>}</FormHelperText>
                            </Box>
                            <Box  m={1} mb={3}>
                                <Typography variant="body1" flexGrow={1}>Confirm your Password</Typography>
                                    <OutlinedInput 
                                        error={ passwordTag }
                                        type="password"
                                        variant="outlined"
                                        id="confirmPassword"
                                        onChange={(event) => setConfirmPassword(event.target.value)} />
                                    <FormHelperText>{passwordTag && <p style={{color: "red"}}>Password don not match</p>}</FormHelperText>
                            </Box>
                        </Box>
                        <Button type="submit" color="secondary" m={1} variant="outlined" fullWidth="true" onClick={handlePasswordOnSubmit}>Submit</Button>
                        <Button color="inherit" m={1} onClick={handleClose} fullWidth="true">Discard</Button>

                        </div>
                    </form>
                </Fade>
            </Modal>
        </div>
    );
}
