import React, { useState, useEffect } from "react";
import AddIcon from '@material-ui/icons/Add';

import { makeStyles, Card, Fab, CardMedia, CardContent, Box, Grid, Container } from '@material-ui/core';
import CreatePostForm from "./CreatePostForm"
import DeletePostButton from "./DeletePostButton"
// import Dropzone from 'react-dropzone'
import Dropzone from "./Dropzone"


const useStyles = makeStyles((theme, buttonHover, previewImages ) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    image: {
      position: "relative",
      display: "flex",
      height: "fit-content"
    },
    margin: {
    margin: theme.spacing(1),
    },
    uploadForm: {
      margin: "2% auto"
    },
    input: {
      margin: 0,
    }
}));

export default function CreatePost() {
  const [previewImages, setPreviewImages] = useState([]);
  const classes = useStyles(previewImages);
  const [images, setImages] = useState([])
  

  const cardCustom = previewImages.map(image => {
    return (
          
      <Grid key={previewImages.indexOf(image)} className={classes.image} item xs={12} sm={3}>
        <CardMedia
          component="img"
          alt="image"
          height="140vh"
          image={image}
        />
        <DeletePostButton images={images} setImages={setImages} previewImages={previewImages} setPreviewImages={setPreviewImages}/>
        </Grid>
    )
  })


 
  return (
    <Container >
      <Dropzone images={images} setImages={setImages} previewImages={previewImages} setPreviewImages={setPreviewImages}/>

      <Grid container justify="center" spacing={2}>
        {cardCustom}
      </Grid>
          {images.length > 0 && 
      <Grid  >
        <Grid item xs={12} sm={7} md={5} className={classes.uploadForm}>
          <Card >
            <CardContent>
              <CreatePostForm images={images}/>
            </CardContent>
          </Card>
        </Grid>
        </Grid>
        }

      <Box display="flex" justifyContent="flex-end" m={2} p={1}>
          <label htmlFor="upload-image-button"> 
            <Fab color="secondary" aria-label="add" component="span" className={classes.margin}>
              <AddIcon />
            </Fab>      
          </label>
      </Box>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
    </Container>
  );
}
