import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles, Card, Fab, CardMedia, CardContent, Box, Grid, Container } from '@material-ui/core';
import CreatePostForm from "./CreatePostForm"


const useStyles = makeStyles((theme, buttonHover, previewImages ) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    image: {
      position: "relative",
      display: "flex",
      height: "fit-content"
    },
    margin: {
    margin: theme.spacing(1),
    },
    uploadForm: {
      margin: "2% auto"
    },
    input: {
      margin: 0,
    },
    iconButton: {
      position: "absolute",
      bottom: "1%",
      right: "1%",
      color: "white"
    },
      deleteIconStyle: buttonHover => {
        if (previewImages)
        return {
          opacity: buttonHover.state === previewImages.indexOf(buttonHover.image) ? "100%" : "80%"
        }
      }

}));

export default function CreatePost() {
  const [buttonHover, setButtonHover] = useState({state: false, image: false })
  const [previewImages, setPreviewImages] = useState([]);
  const classes = useStyles(buttonHover, previewImages);
  const [images, setImages] = useState([])
  
  const handleChange =  e => {
        Array.from(e.target.files).forEach(file => { 
        const reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onloadend = () => {
          if (reader.readyState === 2) {
            setImages(prevImages => prevImages.concat(file))
            setPreviewImages(prevImages => prevImages.concat(reader.result))
          }
        }
      })
    }
  
  const cardCustom = previewImages.map(image => {
    return (
          
      <Grid key = { previewImages.indexOf(image) } className={classes.image} item xs={12} sm={3}>
        <CardMedia
          component="img"
          alt="image"
          height="140vh"
          image={image}
        />
          <IconButton className={classes.iconButton} aria-label="delete" onClick={() => { 
            previewImages.splice(previewImages.indexOf(image), 1); 
            setPreviewImages([...previewImages]); 
            images.splice(previewImages.indexOf(image), 1); 
            setImages([...images]); 
            }}>
            <DeleteIcon 
              onMouseOver={() => setButtonHover({ state: previewImages.indexOf(image), image: image})} 
              onMouseLeave={() => setButtonHover({state: false, image: false})} 
              className={classes.deleteIconStyle}
              />
          </IconButton>
        </Grid>
    )
  })


 
  return (
    <Container >
      <Grid container justify="center" spacing={2}>
        {cardCustom}
      </Grid>
          {/* {images.length > 0 &&  */}
      <Grid  >
        <Grid item xs={12} sm={7} md={5} className={classes.uploadForm}>
          <Card >
            <CardContent>
              <CreatePostForm images={images}/>
            </CardContent>
          </Card>
        </Grid>
        </Grid>
        {/* } */}

      <Box display="flex" justifyContent="flex-end" m={2} p={1}>
          <label htmlFor="upload-image-button"> 
            <Fab color="secondary" aria-label="add" component="span" className={classes.margin}>
              <AddIcon />
            </Fab>      
          </label>
      </Box>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
    </Container>
  );
}
