import React, { useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Axios from "axios";


const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: 0,
        // boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

export default function EditProfileForm(props) {
    const { username, email, webpage, socialMediaAccounts} = props.user
    const { instagram, facebook, snapchat, twitter, flickr } = socialMediaAccounts


    const classes = useStyles();
    const [newUsername, setUsername] = useState("");
    const [newEmail, setEmail] = useState("");
    const [newWebpage, setWebpage] = useState("");
    const [newInstagram, setInstagram] = useState("");
    const [newFacebook, setFacebook] = useState("");
    const [newTwitter, setTwitter] = useState("");
    const [newSnapchat, setSnapchat] = useState("");
    const [newFlickr, setFlickr] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("")
    const [open, setOpen] = React.useState(false);

    const [PasswordTag, setPasswordTag] = useState(
            <TextField
                type="password"
                variant="outlined"
                id="confirmPassword"
                onChange={(event) => setConfirmPassword(event.target.value)} />
                );


    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    function handlePasswordOnSubmit(){
        confirmPassword !== password 
            ? setPasswordTag(<TextField
            type="password"
            error
            helperText="Passwords do not match"
            variant="outlined"
            id="confirmPassword"
            onChange={(event) => setConfirmPassword(event.target.value)} />) 
            : updateProfile()
        }
    

    const updateProfile = async (cb) => {

        await Axios({
            method: "PUT",
            url: `http://localhost:3000/api/edit/${username}`,
            withCredentials: "same-origin",
            data: {
                username: newUsername,
                email: newEmail,
                webpage: newWebpage,
                socialMediaAccounts: {
                    instagram: { account: newInstagram, icon: "instagram"},
                    facebook: { account: newFacebook, icon: "facebook"},
                    twitter: { account: newTwitter, icon: "twitter"},
                    snapchat: { account: newSnapchat, icon:"snapchat"},
                    password: {account: password, icon: "password"},
                    flickr: { account: newFlickr, icon: "flickr"}
                }
            },
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
            },
        })
            .then(function (response) {
                console.log(response.data);
            })
            .then(cb())
    }
// Nota Mirar el css de Modal
    return (
        <div>
            <Button type="button" variant="outlined" onClick={handleOpen}>
                Edit Profile
      </Button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 300,
                }}
            >
                <Fade in={open}>
                    <form>
                    <div className={classes.paper} >
                        <Typography variant="h5" mt={1}>Edit Your Profile</Typography>
                        <Divider/>
                            <Box display="flex" >
                                <Box  m={1}  >
                                    <Typography variant="body1" flexGrow={1}>Username</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={username} id="Username" 
                                onChange={(event) => setUsername(event.target.value)}/>
                                </Box>
                                <Box  m={1}  >
                                    <Typography variant="body1" flexGrow={1}>Email</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={email} 
                                id="email"
                                onChange={(event) => setEmail(event.target.value)}/>
                                </Box>
                            </Box>            
                        <Typography variant="h5">Contact details</Typography>
                        <Divider />
                            <Box display="flex" justifyContent="felex-start" flexWrap="wrap">
                            <Box m={1} >
                                <Typography variant="body1">Web Page</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={webpage} 
                                id="webPage" 
                                onChange={(event) => setWebpage(event.target.value)} />
                            </Box>
                            <Box m={1} >
                                <Typography variant="body1">Instagram</Typography>
                                <TextField 
                                defaultValue={instagram.account} 
                                variant="outlined" id="Instagram" 
                                onChange={(event) => setInstagram(event.target.value)} />
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Facebook</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={facebook.account } 
                                id="Facebook" 
                                onChange={(event) => setFacebook(event.target.value)}/>
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Twitter</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={twitter.account} 
                                id="Twitter" 
                                onChange={(event) => setTwitter(event.target.value)}/>
                            </Box>
                            <Box  m={1} >
                                <Typography variant="body1">Snapchat</Typography>
                                <TextField variant="outlined" 
                                defaultValue={snapchat.account } 
                                id="Snapchat" 
                                onChange={(event) => setSnapchat(event.target.value)} />
                            </Box>
                            <Box  m={1} >
                                    <Typography variant="body1">Flickr</Typography>
                                <TextField 
                                variant="outlined" 
                                defaultValue={flickr.account } 
                                id="Flickr" 
                                onChange={(event) => setFlickr(event.target.value)} />
                            </Box>
                        </Box> 
                    <Typography variant="h5">Change your Password</Typography>
                    <Divider />
                        <Box display="flex">
                            <Box  m={1}  >
                                <Typography variant="body1"  flexGrow={1}>Password</Typography>
                                <TextField
                                        type="password"
                                        variant="outlined"
                                        id="confirmPassword"
                                        onChange={(event) => setPassword(event.target.value)} />
                            </Box>
                            <Box  m={1} mb={3}>
                                <Typography variant="body1" flexGrow={1}>Confirm your Password</Typography>
                                <PasswordTag />
                            </Box>
                        </Box>
                        <Button color="secondary" m={1} variant="outlined" fullWidth="true" onSubmit={handlePasswordOnSubmit}>Submit</Button>
                        <Button color="inherit" m={1} onClick={handleClose} fullWidth="true">Discard</Button>

                        </div>
                    </form>
                </Fade>
            </Modal>
        </div>
    );
}
