import React, { useState, useContext } from "react";
import { makeStyles } from '@material-ui/core/styles';
import AddIcon from '@material-ui/icons/Add';
import Container from '@material-ui/core/Container';
// import { ApiContext } from "../../context/ApiContext";
// import { UserContext } from "../../context/UserContext";
// import FileBase from 'react-file-base64'
// import Axios from "axios"


const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center"
    },
    fileUpload : {  
      border: "1px solid #ccc",
      borderRadius: "50px",
      display: "inline-block",
      padding: "8px 10px",
      cursor: "pointer",
    },
    img: {
      borderRadius: "8px",
      maxWidth: "40vh",
      cursor: "pointer",
    },
    margin: {
    margin: theme.spacing(2),
    },
    filebase: {
      display: "none"
    }

}));

export default function UploadImage(props) {
  // const [image, setImage] = useState({ preview0: "", raw0: "", });
  const [image, setImage] = useState();
  // preview1: "", raw1: "", preview2: "", raw2: "", preview3: "", raw3: "",
  // const user = useContext(UserContext)
  // const api = useContext(ApiContext)

    const classes = useStyles();


  const handleChange = e => {
    if (e.target.files.length) {

      setImage(e.target.files)
      // setImage({
      //   preview0: URL.createObjectURL(e.target.files[0]),
      //   raw0: e.target.files[0],
        // preview1: URL.createObjectURL(e.target.files[1]),
        // raw1: e.target.files[1],
        // preview2: URL.createObjectURL(e.target.files[2]),
        // raw2: e.target.files[2], 
        // preview3: URL.createObjectURL(e.target.files[3]),
        // raw3: e.target.files[3],
      // });
    }
    console.log(image);
    // handleUpload();
  };
  // const {username, _id} = user.data

  // const handleUpload = async e => {
  //   e.preventDefault();
  //   const imageFormData = new FormData(document.getElementById("uploadImage"));

  //   // formData.append(date, username)
  //   for (const p of imageFormData){
  //         console.log("image log", p);
  //     }

  //     // api.createPost(post)
  //   await Axios.post(`http://localhost:3000/api/upload-image/${username}/${_id}`, { author: username })
  //   .then(
  //   await Axios({
  //         method: "POST",
  //         url: `http://localhost:3000/api/upload-image/${_id}`,
  //         withCredentials: "same-origin",
  //         data: imageFormData,
  //         headers: {
  //             "Accept": "multipart/form-data",
  //             "Content-Type": "application/x-www-form-urlencoded",
  //         },
  //         })
  //         .then(function (response) {
  //             console.log(response.data);
  //         })
  //     )
  //   } 
  return (
      <div className={classes.root}>
      <Container m>
      <form id="uploadImage">
      <label htmlFor="upload-image-button">
            {/* {image.preview ? (
              <img src={image.preview} alt="dummy" className={classes.img} /> */}
                      {image ? (
              <img src={URL.createObjectURL(image)} alt="dummy" className={classes.img} />
        ) : (
          <>
            <h5 className={classes.fileUpload}><AddIcon/></h5>
          </>   
        )}
      </label>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        style={{ display: "none" }}
        onChange={handleChange}
      />
          {/* <FileBase
            type="file"
            multiple={true}
            className={classes.filebase}
            id="upload-image-button"
            name="uploadImage"
            style={{ display: "none" }}
            onDone={({ base64 }) => console.log(base64) }
            // setProfileData({...profileData, avatar: base64 })
          /> */}
          {/* <button style={{alignItems: "center"}} onClick={handleUpload}>Upload</button> */}
      <br />
      </form>
      </Container>
    </div>
  );
}
