import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import Button from '@material-ui/core/Button';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        borderRadius: 0,
        backgroundColor: "#F5EB00"
    },
    icons: {
        color: "#000"
    },
    title:{
        fontFamily: "Abril Fatface",
        fontSize: "8vh"
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export default function Dashboard(props) {
    const classes = useStyles();
    // const [expanded, setExpanded] = React.useState(false);
    // console.log(props.auth);
    // const handleExpandClick = () => {
    //     setExpanded(!expanded);
    // };
    console.log(props.user.auth);

    return (
        <div>
        <Card variant="outlined" className={classes.root}>
            <CardHeader
                avatar={
                    <Avatar variant="rounded" ></Avatar>
                 }
                title={<Typography className={classes.title} variant="h3" color="textPrimary" component="p">
                  <h3>{props.user.username}</h3>
                </Typography>}
        //         avatar={
        //             <Avatar aria-label="recipe" className={classes.avatar}>
        //                 R
        //   </Avatar>
        //         }
        //         // action={
        //         //     <IconButton aria-label="settings">
        //         //         <MoreVertIcon />
        //         //     </IconButton>
        //         // }
        //         title="Shrimp and Chorizo Paella"
        //         subheader="September 14, 2016"
            />
            {/* <CardContent>
                
            </CardContent> */}
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                    <FavoriteIcon className={classes.icons} />
                </IconButton>
                <Typography aria-label="add to favorites">
                    <Button variant="body2"> instagram</Button>
                </Typography>
                <Typography aria-label="add to favorites">
                    <Button variant="body2"> Facebook</Button>
                </Typography>
                <Typography aria-label="add to favorites">
                    <Button variant="body2"> Flickr</Button>
                </Typography>
                <IconButton className={classes.icons} aria-label="share">
                    <ShareIcon />
                </IconButton>

            </CardActions>

        </Card>
            <Button color="secondary" variant="outlined"> Upload img </Button>
        </div>
    );
}
