import React from 'react';
import CameraIcon from '@material-ui/icons/Camera';
import TwitterIcon from '@material-ui/icons/Twitter';
import FacebookIcon from '@material-ui/icons/Facebook';
import InstagramIcon from '@material-ui/icons/Instagram';
import CropPortraitIcon from '@material-ui/icons/CropPortrait';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import Button from '@material-ui/core/Button';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import { logDOM } from '@testing-library/react';

// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        borderRadius: 0,
        // backgroundColor: "#F5EB00"
    },
    icons: {
        color: "#000",
        fontSize: "2vh",
    },
    title:{
        fontFamily: "Abril Fatface",
        // fontSize: "4vh",
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export default function Dashboard(props) {
    const classes = useStyles();
    const {socialMediaAccounts, username} = props.user

        function icon(icon){
            if (icon === "flickr") return <CameraIcon  className={classes.icons}/>
            if (icon === "Twitter") return <TwitterIcon  className={classes.icons}/>
            if (icon === "Facebook") return <FacebookIcon  className={classes.icons}/>
            if (icon === "Instagram") return <InstagramIcon  className={classes.icons}/>
            if (icon === "snapchat") return <CropPortraitIcon  className={classes.icons}/>
        }
    return (
        <div>
        <Card variant="outlined" className={classes.root}>
            <CardHeader
                avatar={
                    <Avatar variant="rounded" ></Avatar>
                 }
                title={<Typography className={classes.title} color="textPrimary" >
                  <h1>||{username}</h1>
                </Typography>}
            />
            <CardActions disableSpacing>
                <IconButton aria-label="add to ">
                        <FavoriteIcon style={{color: "000"}} />
                </IconButton>

                    {Object.entries(socialMediaAccounts).map(
                        function ([key, value]) {
                            console.log("key " + key, "value" +  value)
                            return <Typography variant="subtitle2"> {icon(value.icon)} <strong>{key} </strong>{value.account} &nbsp;</Typography> // &nbsp; stands for non-breaking space, to add an space in between 
                        })}
                    {/* <ShareIcon /> */}
            </CardActions>

        </Card>
            <Button color="secondary" variant="outlined"> Upload img </Button>
        </div>
    );
}
