import Axios from "axios";

class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    
    async logout() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        }

        await Axios.get("http://localhost:3000/api/logout", options).then (response => {
            console.log(response);
            // this.props.history.push("/")
        })
            .then(this.authenticated = false)   
    }

    async isLoggedIn(cb) {

        // var authentication = false
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
        await Axios.get("http://localhost:3000/api/isAuth", options)
        
                .then(response => { console.log(response.data);
                //    return response.data.auth
                    if(response.data === true){
                        this.login(() => {return this.authenticated})
                    }  
                })
                    
                // .then(() => { this.authenticated = authentication })
                // .finally(console.log(this.authenticated))
        
            cb()
        // return authentication; 
        }
            
    isAuthenticated() {
        this.isLoggedIn(()=>{console.log(this.authenticated); })   
        // return(this.authenticated);
    }
    
}

export default new Auth();
