import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import Button from '@material-ui/core/Button';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        borderRadius: 0,
        // backgroundColor: "#F5EB00"
    },
    icons: {
        color: "#000"
    },
    title:{
        fontFamily: "Abril Fatface",
        // fontSize: "4vh",
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export default function Dashboard(props) {
    const classes = useStyles();
    const {socialMedia, username} = props.user

    console.log(username);

    return (
        <div>
        <Card variant="outlined" className={classes.root}>
            <CardHeader
                avatar={
                    <Avatar variant="rounded" ></Avatar>
                 }
                title={<Typography className={classes.title} color="textPrimary" >
                  <h1>||{props.user.username}</h1>
                </Typography>}
            />
            <CardActions disableSpacing>
                <IconButton aria-label="add to ">
                    <FavoriteIcon className={classes.icons} />
                </IconButton>
                <Typography variant="subtitle2">
                    {/* {socialMedia.map(socialMediaAccount => {
                        return( 
                            <Button>{socialMediaAccount}</Button>
                        )
                    })} */}
                     <p>INSTAGRAM Â· </p> 
                </Typography>
                <Typography variant="subtitle2">
                     <p>FACEBOOK  </p> 
                </Typography>
                <Typography variant="subtitle2" >
                    <p>FLICKR </p> 
                </Typography>
                <IconButton className={classes.icons} aria-label="share">
                    <ShareIcon />
                </IconButton>

            </CardActions>

        </Card>
            <Button color="secondary" variant="outlined"> Upload img </Button>
        </div>
    );
}
