import createMuiTheme from '@material-ui/core/styles';
import purple from '@material-ui/core/colors/purple';


const theme = createMuiTheme({
    typography: {
        useNextVariants: true,
    },
    palette: {
        primary: {
            light: '#63ccff',
            main: purple,
            dark: '#006db3',
            contrastText: '#fff',
        },
    },
    shape: {
        borderRadius: 8
    }
});

export default theme;
