import Axios from "axios";
import Api from "../axiosConfig"


class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    
    logout(cb) {
        Axios.get(Api + "/api/logout").then (response => {
            console.log(Api);
        })
        this.authenticated = false;
        cb();
    }

    isAuthenticated() {
        return this.authenticated;
    }
}

export default new Auth();
