import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import {  makeStyles, Card, Fab, CardMedia, CardContent,  Box, Grid, Container, } from '@material-ui/core';
import CreatePostForm from "./CreatePostForm"


const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    img: {
      maxWidth: "30vh",
      height: "30vh"
    },
    margin: {
    margin: theme.spacing(1),
    },
    card: {
      maxWidth: "440vh",
    },
    input: {
      margin: 0,

    }

}));

export default function CreatePost() {
  const [images, setImages] = useState([]);
  const classes = useStyles();
  const arrayOfImages = []
  class Image { constructor(base64Data){ this.base64Data = base64Data }
 }
  const handleChange = async e => {
       await Array.from(e.target.files).forEach(file => { 
        const reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onloadend = () => {
          if (reader.readyState === 2) {
            const image = new Image(reader.result)
            arrayOfImages.push(image)
            setImages(prevImages => prevImages.concat(reader.result))
            return arrayOfImages
          }
        }
      })
    }
  

  const cardCustom = images.map(image => {
    return (
              <CardMedia
                component="img"
                alt="image"
                className={classes.img}
                image={image}
              />
    )
  })

 
  return (
    <Container maxWidth="200vh">
      <Grid container justify="center" spacing={2}>
        <Card className={classes.card} m={2} p={1}>
              {cardCustom}
          {images.length > 1 && 
          <CardContent>
              <Grid  xs={3} >
                <CreatePostForm images={images}/>
              </Grid>
          </CardContent>}
        </Card>
      </Grid>

      <Box display="flex" justifyContent="flex-end" m={2} p={1}>
          <label htmlFor="upload-image-button"> 
            <Fab color="secondary" aria-label="add" component="span" className={classes.margin}>
              <AddIcon />
            </Fab>      
          </label>
      </Box>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
    </Container>
  );
}
