import { useContext, useEffect } from "react"
import { ApiContext } from "../context/ApiContext";

export default function User() {
    const api = useContext(ApiContext)
    
    let user = {}
    useEffect((user) => {
        api.getUser().then(response => user = response) 
            .then(response => {
            user = response.data 
            localStorage.setItem("tokens", user.auth)
            // cb(user)
            // .then()
        }, [user])

    })
    return (
        user
    )
}
