import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import AddIcon from '@material-ui/icons/Add';
import Axios from "axios";

const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center"
    },
    fileUpload : {  
      border: "1px solid #ccc",
      borderRadius: "50px",
      display: "inline-block",
      padding: "8px 10px",
      cursor: "pointer",
    },
    img: {
      borderRadius: "8px",
      maxWidth: "40vh",
      cursor: "pointer",
      margin: "2vh auto"
    }

}));

export default function UploadImage(props) {
  const [image, setImage] = useState({ preview: "", raw: ""});

    const classes = useStyles();


  const handleChange = e => {
    if (e.target.files.length) {
      setImage({
        preview: URL.createObjectURL(e.target.files[0]),
        raw: e.target.files[0],  
      });
    }
  };
  const {username, _id} = props.user

  const handleUpload = async e => {
    e.preventDefault();
    const formData = new FormData(document.getElementById("form"));

    // formData.append(date, username)
      for (const p of formData){
          console.log(p);
      }
    await Axios.post(`http://localhost:3000/api/upload-image/${username}/${_id}`, { author: username })
    .then(
      await Axios({
          method: "POST",
          url: `http://localhost:3000/api/upload-image/${_id}`,
          withCredentials: "same-origin",
          data: formData,
          headers: {
              "Accept": "multipart/form-data",
              "Content-Type": "application/x-www-form-urlencoded",
          },
      })
          .then(function (response) {
              console.log(response.data);
          })
    )
        }
  return (
      <div className={classes.root}>
      <form id="form">
      <label htmlFor="upload-button">
        {image.preview ? (
        <img src={image.preview} alt="dummy" className={classes.img}/>
        ) : (
          <>
            {/* <span className="fa-stack fa-2x mt-3 mb-2">
              <i className="fas fa-circle fa-stack-2x" />
              <i className="fas fa-store fa-stack-1x fa-inverse" />
            </span> */}
            <h5 className={classes.fileUpload}><AddIcon/></h5>
          </>   
        )}
      </label>
      <input
        type="file"
        id="upload-button"
        name="image"
        style={{ display: "none" }}
        onChange={handleChange}
      />
          <button style={{alignItems: "center"}} onClick={handleUpload}>Upload</button>
      <br />
      </form>
    </div>
  );
}
