import React, { useState, useContext } from "react";
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";

import { InputLabel, InputAdornment, Input, Button, Grid, Link } from '@material-ui/core';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import LabelIcon from '@material-ui/icons/Label';



export default function CreatePostForm(props) {
    const [tags, setTags] = useState([]);
    const [inputValue, setInputValue] = useState("");
    const [price, setPrice] = useState();
    const user = useContext(UserContext)
    const api = useContext(ApiContext)
    const { username, _id } = user.data

    const tagsTest = []


    const handleUpload = async e => {


        const post = {
            creator: username,
            creatorId: _id,
            image: props.images,
            created: new Date().getDay(),
            price: price,
            // tags: tags
        }

        api.createPost(post)
    }
    
    const handleKeyDown = (e) => {
        // e.preventDefault()
        e.key === 'Enter' && tags.push(...tags, e.target.value)
        console.log(tags);
    }
    const handleTags = (e) => {
        // e.preventDefault()
        setTags(prevTags => prevTags.concat(inputValue))
        // tags.push(...tags, inputValue)
        console.log(tags)

        tags.map(tag => 
            console.log(tag)
            )
        }

    // const tagsArray = tags.map(tag => {
    //     return (
    //         <Link href={tag} onClick={(e) => e.preventDefault()} variant="body2">
    //             {" #" + tag}
    //         </Link>
    //     )
    // })
    return (
        <div>
            <Grid item >
                <InputLabel style={{ fontSize: 13 }}>Choose a price</InputLabel>
                <Input
                    id="Price"
                    fullWidth
                    placeholder="5$"
                    type="number"
                    onChange={(e) => setPrice( ...tags, e.target.value )}
                    startAdornment={ <InputAdornment position="start"> <MonetizationOnIcon /> </InputAdornment> }
                />
            </Grid>
            <br />
            <Grid item>
                <InputLabel style={{ fontSize: 13 }}>Tags</InputLabel>
                <Input
                    fullWidth
                    id="Tags"
                    value={inputValue}
                    placeholder="Red dress..."
                    onChange={(e) => setInputValue(e.target.value)}
                    // onKeyDown={handleKeyDown}
                    startAdornment={ <InputAdornment position="start"> <LabelIcon /> </InputAdornment> }
                    endAdornment={ <Button onClick={handleTags} > ADD </Button> }
                />
            </Grid>
            <br />
            <Grid item>
                    {/* {tagsArray && tagsArray} */}
            </Grid>
            <br />
            <Grid item>
                <Button
                    variant="contained"
                    color="default"
                    fullWidth
                    startIcon={<CloudUploadIcon />}
                    onClick={handleUpload}
                >
                    Upload
                </Button>
            </Grid>
        </div>
    )
}
