import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: 0,
        // boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

export default function EditProfileForm() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <Button type="button" variant="outlined" onClick={handleOpen}>
                Edit Profile
      </Button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 300,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper} style={{ width: '80%' }}>
                        <Box display="flex" justifyContent="center">
                                <Box mx={1} my={1} width="50%" flexWrap="nowrap">
                                    <Typography variant="body1" flexGrow={1}>Username</Typography><TextField id="Username" label="" />
                                </Box>
                                <Box mx={1} my={1} width="50%">
                                   <Typography variant="body1">Instagram</Typography> <TextField id="Instagram" label=""  />
                                </Box>
                                <Box mx={1} my={1} width="50%">
                                    <Typography variant="body1">Facebook</Typography><TextField id="Facebook" label="" />
                                </Box>
                                <Box mx={1} my={1} width="50%">
                                    <Typography variant="body1">Instagram</Typography><TextField id="Twitter" label="" />
                                </Box>
                                <Box mx={1} my={1} width="50%">
                                    <Typography variant="body1">Twitter</Typography><TextField id="Snapchat" label="" />
                                </Box>
                                <Box mx={1} my={1} width="50%">
                                    <Typography variant="body1">Flickr</Typography> <TextField id="Flickr" label="" />
                                </Box>
                        </Box>            
                    </div>
                </Fade>
            </Modal>
        </div>
    );
}
