import Axios from "axios";

class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    async logout() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        }

        await Axios.get("http://localhost:3000/api/logout", options).then (response => {
            console.log(response);
            // this.props.history.push("/")
        })
            .then(this.authenticated = false)   
    }

    async pepe(cb) {

        // var authentication = false
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
        await Axios.get("http://localhost:3000/api/isAuth", options)
        
                                    .then(response => {
                                        console.log(response.data);
                                        if (response.data === true) {
                                            this.login(()=>{console.log("Debería estar loggedIn " + this.authenticated)})
                                        }//this.authenticated = response.data
                                    })
                                    return this.authenticated
        cb()
        }
            
    isAuthenticated() {
        this.pepe(() => { console.log( this.authenticated) })
        console.log("linea 54 " + this.pepe(()=>{return this.authenticated}));
        // this.pepe(this.authenticated)
    }
    
}

export default new Auth();
