import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import AddIcon from '@material-ui/icons/Add';
import Axios from "axios";

const useStyles = makeStyles((theme) => ({
    root: {
        display: "flex",
        alignItems: "center"
    },
    fileUpload : {
    border: "1px solid #ccc",
    borderRadius: "50px",
    display: "inline-block",
    padding: "8px 10px",
    cursor: "pointer",
},
    img: {
        borderRadius: "20px",
        maxWidth: "vh40"
    }

}));

export default function UploadImage(props) {
  const [image, setImage] = useState({ preview: "", raw: "" });

    const classes = useStyles();


  const handleChange = e => {
    if (e.target.files.length) {
      setImage({
        preview: URL.createObjectURL(e.target.files[0]),
        raw: e.target.files[0]
      });
    }
  };

  const handleUpload = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append(image, image.raw)

    console.log(formData, image.raw);
      await Axios({
          method: "POST",
          url: `http://localhost:3000/api/upload-image/${props.userId}`,
          withCredentials: "same-origin",
          data: {image: image, raw: image.raw},
          headers: {
              "Accept": "application/json",
          },
      })
          .then(function (response) {
              console.log(response.data);
          })

        }
  return (
      <div className={classes.root}>
      <label htmlFor="upload-button">
        {image.preview ? (
        <img src={image.preview} alt="dummy" className={classes.img}/>
        ) : (
          <>
            <span className="fa-stack fa-2x mt-3 mb-2">
              <i className="fas fa-circle fa-stack-2x" />
              <i className="fas fa-store fa-stack-1x fa-inverse" />
            </span>
            <h5 className={classes.fileUpload}><AddIcon/></h5>
          </>   
        )}
      </label>
      <input
        type="file"
        id="upload-button"
        style={{ display: "none" }}
        onChange={handleChange}
      />
          <button style={{alignItems: "center"}} onClick={handleUpload}>Upload</button>
      <br />
    </div>
  );
}
