import React, { useState, useContext, useEffect } from "react";
import { makeStyles } from '@material-ui/core/styles';
import AddIcon from '@material-ui/icons/Add';
import Container from '@material-ui/core/Container';
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
// import FileBase from 'react-file-base64'
// import Axios from "axios"


const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      alignItems: "center",
      borderRadius: "0"
    },
    fileUpload : {  
      border: "1px solid #ccc",
      borderRadius: "50px",
      display: "inline-block",
      padding: "8px 10px",
      cursor: "pointer",
    },
    img: {
      borderRadius: "8px",
      maxWidth: "140vh",
      cursor: "pointer",
    },
    margin: {
    margin: theme.spacing(2),
    },
    filebase: {
      display: "none"
    }

}));

export default function CreatePost(props) {
  // const [image, setImage] = useState({ preview0: "", raw0: "", });
  const [images, setImages] = useState([]);
  // preview1: "", raw1: "", preview2: "", raw2: "", preview3: "", raw3: "",
  const user = useContext(UserContext)
  const api = useContext(ApiContext)
  const classes = useStyles();
  const arrayOfImages = []
  class Image { constructor(base64Data){ this.base64Data = base64Data }
 }
  const handleChange = async e => {
       await Array.from(e.target.files).forEach(file => { 
        const reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onloadend = () => {
          if (reader.readyState === 2) {
            const image = new Image(reader.result)
            arrayOfImages.push(image)
            setImages(prevImages => prevImages.concat(reader.result))
            return arrayOfImages
          }
        }
      })
    }

    // handleUpload();
  const {username, _id} = user.data

  const handleUpload = async e => {


    const post = {
      creator: username,
      creatorId: _id,
      // image: image,
      created: new Date().getDay(),
      // price: price,
      // tags: tags
    }
    
    api.createPost(post)
  }

  console.log(images[2]);
  const cardCustom = images.map(image => {
    return (
        <Grid item xs={10} sm={3}>
          <CardActions>
            <CardActionArea>
              <CardMedia
                component="img"
                alt="Contemplative Reptile"
                height="180vh"
                image={image}
              />

            </CardActionArea>
          </CardActions>
        </Grid>

    )
  })

 
  return (
      <div className={classes.root}>
      <Container >
      <form id="uploadImage">
      <label htmlFor="upload-image-button">
            <Card className={classes.root}>
              <Grid container spacing={3}>
                {cardCustom}
              </Grid>
            </Card>
            <h5 className={classes.fileUpload}><AddIcon />
            </h5>
            {images && <Button size="small" style={{ alignItems: "center" }} onClick={handleUpload}>
              Upload
            </Button>}
      </label>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
      <br />
      </form>
      </Container>
    </div>
  );
}
