import Axios from "axios";

class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    async logout() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        }

        await Axios.get("http://localhost:3000/api/logout", options).then (response => {
            console.log(response);
            // this.props.history.push("/")
        })
            .then(this.authenticated = false)   
    }

     pepe(cb) {

        // var authentication = false
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
         Axios.get("http://localhost:3000/api/isAuth", options)
        
                                    .then(response => {
                                        console.log(response.data);
                                        if (response.data === true) {
                                            this.login(()=>{console.log("Debería estar loggedIn " + this.authenticated)})
                                        }//this.authenticated = response.data
                                    })
                                    .then(()=>{console.log(this.authenticated)})
        cb()
        }
            
    isAuthenticated() {
        // this.pepe(() => { console.log("linea 52 " + this.authenticated) })
        
        this.pepe(() => {console.log("Linea 55 " + this.authenticated);})
    }
    
}

export default new Auth();
