import React, { useState, useContext, useRef } from "react";
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";

import { InputLabel, InputAdornment, Input, Button, Grid, Link } from '@material-ui/core';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import LabelIcon from '@material-ui/icons/Label';



export default function CreatePostForm(props) {
    const user = useContext(UserContext)
    const api = useContext(ApiContext)
    const { username, _id } = user.data
    const [tags, setTags] = useState([]);
    const [inputField, setInputField] = useState({
        price: "",
        tags: ""
    })


    const handleUpload = async e => {


        const post = {
            creator: username,
            creatorId: _id,
            image: props.images,
            created: new Date().getDay(),
            // price: price,
            // tags: tags
        }

        api.createPost(post)
    }
    const handleState = (e) => {
        setInputField({ tags: "" })
    }

    const handleKeyDown = (e) => {
        e.key === 'Enter' && setTags(prevTags => prevTags.concat("#" + e.target.value));
        // setInputField({tags: ""})
    }

    const handleInputField = (e) => {
        setTags(prevTags => prevTags.concat(" #" + e.target.value));
        setInputField({tags: e.target.value})
    }
    return (
        <form >
            <Grid item >
                <InputLabel style={{ fontSize: 13 }}>Choose a price</InputLabel>
                <Input
                    id="Price"
                    style={{ width: "100%" }}
                    placeholder="5$"
                    name="price"
                    type="number"
                    // onChange={(e) => setTags( ...tags, e.target.value )}
                    startAdornment={
                        <InputAdornment position="start">
                            <MonetizationOnIcon />
                        </InputAdornment>
                    }
                   
                />
            </Grid>
            <br />
            <Grid item>
                <InputLabel style={{ fontSize: 13 }}>Tags</InputLabel>
                <Input
                    style={{ width: "100%" }}
                    id="Tags"
                    value={inputField.tags}
                    name="tags"
                    placeholder="Red dress..."
                    onKeyDown={handleKeyDown}
                    onChange={handleInputField}
                    onSubmit={handleState}
                    startAdornment={
                        <InputAdornment position="start">
                            <LabelIcon />
                        </InputAdornment>
                    }
                    endAdornment={
                        <Button >
                            ADD
                        </Button>
                    }
                />
            </Grid>
            <br />
            <Grid item>
                    {tags && tags.map(tag => 
                        <Link href={tag} key={tag} onClick={(e) => e.preventDefault()} color="secondary" variant="body2">
                            {tag}
                        </Link>
                    )}
            </Grid>
            <br />
            <Grid item>
                <Button
                    variant="contained"
                    color="default"
                    fullWidth
                    startIcon={<CloudUploadIcon />}
                    onClick={handleUpload}
                >
                    Upload
                </Button>
            </Grid>
        </form>
    )
}
