            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
        await Axios.get("http://localhost:3000/api/isAuth", options)
        
                                    .then(response => {
                                        console.log(response.data);
                                        if (response.data === true) {
                                            this.login(()=>{console.log("Debería estar loggedIn " + this.authenticated)})
                                        }//this.authenticated = response.data
                                    }) 
                                    return "Returned value " + this.authenticated
        // cb()
        }
            
      isAuthenticated() {
         this.pepe().then(function (result) {
             console.log(result);
            //  $("#output").append(result);
         });
        // this.pepe()
        // return this.authenticated
    }
    
}

export default new Auth();
