import Axios from "axios";

class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    
    async logout() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        }

        await Axios.get("http://localhost:3000/api/logout", options).then (response => {
            console.log(response);
            // this.props.history.push("/")
        })
            .then(this.authenticated = false)   
    }

    async checkAuth() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
        await Axios.get("http://localhost:3000/api/isAuth", options).then(response => {
           console.log(response.data);
        //    return response.data.auth
            response.data.auth ? this.authenticated = true : this.authenticated = false 
        })
        return this.authenticated //response.data.auth
        // console.log("esta auth " + this.authenticated);
    }

    isAuthenticated() {
        return this.checkAuth()
    }
    
}

export default new Auth();
