import {React, useState} from "react";
import  {withRouter}  from "react-router";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import Drawer from "@material-ui/core/Drawer";
import Button from "@material-ui/core/Button";
import useMediaQuery from "@material-ui/core/useMediaQuery"; 

// import router from "../../server/api/logout";

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    title: {
        flexGrow: 1
    },
    headerOptions: {
        marginRight: "3vw",
        justifyContent: "space-evenly"
    }
}));

const Header = props => {
    const { history } = props;
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = useState({
        top: false,
        left: false,
        bottom: false,
        right: false,});
    const open = Boolean(anchorEl);
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

    const handleMenu = event => {
        // setAnchorEl(event.currentTarget);
        setAnchorEl({ right: true });

    };

    const handleMenuClick = pageURL => {
        history.push(pageURL);
        setAnchorEl(null);
    };

    const handleButtonClick = pageURL => {
        history.push(pageURL);
    };

    const menuItems = [
        {
            key: 0,
            menuTitle: "Home",
            pageURL: "/"
        },
        {
            key: 1,
            menuTitle: "Login",
            pageURL: "/login"
        },
        {
            key: 2,
            menuTitle: "Sign up",
            pageURL: "/signup"
        },
        {
            key: 3,
            menuTitle: "Dashboard",
            pageURL: "/dashboard"
        }
    ];

    return (
        <div className={classes.root}>
                   
            <AppBar position="static">
                <Toolbar>
                    <Typography color="initial"  onClick={() => handleButtonClick("/")} className={classes.title}>
                        <label style={{ fontFamily: "Bebas Neue", fontSize: "40px"}}>Photure</label>
                        </Typography>
                    {isMobile ? (
                        <>
                            <IconButton
                                edge="start"
                                className={classes.menuButton}
                                color="inherit"
                                aria-label="menu"
                                onClick={handleMenu}
                            >
                                <MenuIcon />
                            </IconButton>
                            <Drawer 
                                id="menu-drawer"
                                anchorEl={anchorEl}
                                anchorOrigin={"right"}
                                keepMounted
                                transformOrigin={{
                                    vertical: "top",
                                    horizontal: "right"
                                }}
                                open={open}
                                onClose={() => setAnchorEl({right: false})}
                            >
                                {menuItems.map(menuItem => {
                                    const { key, menuTitle, pageURL } = menuItem;
                                    return (
                                        <MenuItem key={key} onClick={() => handleMenuClick(pageURL)}>
                                            {menuTitle}
                                        </MenuItem>
                                    );
                                })}
                            </Drawer>
                        </>
                    ) : (
                            <div className={classes.headerOptions}>
                                <Button
                                    variant="outlined"
                                    color="secondary"
                                    onClick={() => handleButtonClick("/signup")}
                                >
                                    SING UP
              </Button>
                                <Button
                                    color="inherit"
                                    onClick={() => handleButtonClick("/login")}
                                >
                                    LOGIN
              </Button>
                            </div>
                        )}
                </Toolbar>
            </AppBar>
        </div>
    );
};

export default withRouter(Header);
