import React from 'react';
import CameraIcon from '@material-ui/icons/Camera';
import TwitterIcon from '@material-ui/icons/Twitter';
import FacebookIcon from '@material-ui/icons/Facebook';
import InstagramIcon from '@material-ui/icons/Instagram';
import CropPortraitIcon from '@material-ui/icons/CropPortrait';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import ShareIcon from '@material-ui/icons/Share';
import EditProfileForm from "./EditProfileForm"

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        borderRadius: 0,
        // backgroundColor: "#F5EB00"
    },
    icons: {
        color: "#000",
        fontSize: "2vh",
    },
    title:{
        fontFamily: "Abril Fatface",
    },

}));

export default function Dashboard(props) {
    const classes = useStyles();
    const {socialMediaAccounts, username} = props.user
    const accounts = []
    function icon(icon){
        if (icon === "Flickr") return <CameraIcon  className={classes.icons}/>
        if (icon === "Twitter") return <TwitterIcon  className={classes.icons}/>
        if (icon === "Facebook") return <FacebookIcon  className={classes.icons}/>
        if (icon === "Instagram") return <InstagramIcon  className={classes.icons}/>
        if (icon === "Snapchat") return <CropPortraitIcon  className={classes.icons}/>
    }

    for (const [index, value] of Object.entries(socialMediaAccounts)) {
        for (const [key, val] of Object.entries(value)) {
            const {account, icon} = val

            accounts.push(<Box key={index}>
                    {/* {icon(icon)} */}
                    <Typography variant="body1">{key}</Typography>
                    {account}</Box>)
        }
    }
    return (
        <div>

            {/* ----------------------------------   Header  ----------------------------------- */}
        <Card variant="outlined" className={classes.root}>
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardHeader
                        avatar={
                            <Avatar variant="rounded" ></Avatar>
                        }
                        title={<Typography className={classes.title} color="textPrimary" >
                        <h1>|{username}</h1>
                        </Typography>}
                        />
                </Box>
                    <Box mx={4} my={6}>
                        <EditProfileForm user={props.user}/>
                    </Box>
            </Box>
        <Box style={{ width: '100%' }} >
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardActions >
                        {accounts}
                    </CardActions> 
                </Box>
                        <Box mx={4}><ShareIcon /></Box>
            </Box>
        </Box>
        </Card>
            {/* ----------------------------------   Photos Section  ----------------------------------- */}
            <Button color="secondary" variant="outlined"> Upload img </Button>
        </div>
    );
}
