  const handleUpload = async e => {
      }

  //     // api.createPost(post)
  //   await Axios.post(`http://localhost:3000/api/upload-image/${username}/${_id}`, { author: username })
  //   .then(
  //   await Axios({
  //         method: "POST",
  //         url: `http://localhost:3000/api/upload-image/${_id}`,
  //         withCredentials: "same-origin",
  //         data: imageFormData,
  //         headers: {
  //             "Accept": "multipart/form-data",
  //             "Content-Type": "application/x-www-form-urlencoded",
  //         },
  //         })
  //         .then(function (response) {
  //             console.log(response.data);
  //         })
  //     )
  //   } 

      // function FileBase64(){
      //   base64Images.map(image => <img src={image} alt="post" className={classes.img} /> )
      // }

  return (
      <div className={classes.root}>
      <Container m="true">
      <form id="uploadImage">
      <label htmlFor="upload-image-button">
            {/* {images.map(image => <img src={image} alt="postImage" />) } */}
            <Card className={classes.root}>
              <CardActionArea>
                <CardMedia
                  component="img"
                  alt="Contemplative Reptile"
                  height="140"
                  image="/static/images/cards/contemplative-reptile.jpg"
                  title="Contemplative Reptile"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    Lizard
          </Typography>
                  <Typography variant="body2" color="textSecondary" component="p">
                    Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                    across all continents except Antarctica
          </Typography>
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  Share
        </Button>
                <h5 className={classes.fileUpload}><AddIcon /></h5>
              </CardActions>
            </Card>
            <img src={images[0]} alt="postImage" />
      </label>
      <input
        type="file"
        id="upload-image-button"
        name="uploadImage"
        multiple
        style={{ display: "none" }}
        onChange={handleChange}
      />
          {/* <FileBase
            type="file"
            multiple={true}
            className={classes.filebase}
            id="upload-image-button"
            name="uploadImage"
            style={{ display: "none" }}
            onDone={({ base64 }) => console.log(base64) }
            // setProfileData({...profileData, avatar: base64 })
          /> */}
          {/* <button style={{alignItems: "center"}} onClick={handleUpload}>Upload</button> */}
      <br />
      </form>
      </Container>
    </div>
  );
}
