import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';


const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: 0,
        // boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

export default function EditProfileForm() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <Button type="button" variant="outlined" onClick={handleOpen}>
                Edit Profile
      </Button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 300,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper} >
                        <Typography variant="h5" mt={1}>Edit Your Profile</Typography>
                            <Divider/>
                        <Box display="flex" >
                            <Box  m={1}  >
                                <Typography variant="body1" flexGrow={1}>Username</Typography><TextField variant="outlined" id="Username" label="" />
                            </Box>
                            <Box  m={1}  >
                                <Typography variant="body1" flexGrow={1}>Email</Typography><TextField variant="outlined" id="Username" label="" />
                            </Box>
                        </Box>            
                        <Typography variant="h5">Contact details</Typography>
                        <Divider />
                            <Box display="flex" justifyContent="felex-start" flexWrap="wrap">
                                <Box  m={1} >
                                <Typography variant="body1">Instagram</Typography> <TextField defaultValue="@" variant="outlined" id="Instagram" label=""  />
                                </Box>
                                <Box  m={1} >
                                    <Typography variant="body1">Facebook</Typography><TextField variant="outlined" id="Facebook" label="" />
                                </Box>
                                <Box  m={1} >
                                    <Typography variant="body1">Instagram</Typography><TextField variant="outlined" id="Twitter" label="" />
                                </Box>
                                <Box  m={1} >
                                <Typography variant="body1">Twitter</Typography><TextField defaultValue="@" variant="outlined" id="Snapchat" label="" />
                                </Box>
                                <Box  m={1} >
                                    <Typography variant="body1">Flickr</Typography> <TextField variant="outlined" id="Flickr" label="" />
                                </Box>
                        </Box> 
                        <Typography variant="h5">Change your Password</Typography>
                        <Divider />
                        <Box display="flex">
                            <Box  m={1}  >
                                <Typography variant="body1" flexGrow={1}>Password</Typography><TextField type="password" variant="outlined" id="Username" label="" />
                            </Box>
                            <Box  m={1} mb={3}>
                                <Typography variant="body1" flexGrow={1}>Confirm your Password</Typography><TextField type="password" variant="outlined" id="Username" label="" />
                            </Box>
                        </Box>
                        <Button color="secondary" m={1}  variant="outlined" fullWidth="true">Submit</Button>
                        <Button color="inherit" m={1} onClick={() => handleClose()} fullWidth="true">Discard</Button>
                    </div>
                </Fade>
            </Modal>
        </div>
    );
}
