import Axios from "axios";

class Auth {
    constructor() {
        this.authenticated = false;
    }

    login(cb) {
        this.authenticated = true;
        cb();
    }
    
    async logout() {
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        }

        await Axios.get("http://localhost:3000/api/logout", options).then (response => {
            console.log(response);
            // this.props.history.push("/")
        })
            .then(this.authenticated = false)   
    }

    isAuthenticated() {

        let authentication = false
        const options = {
            withCredentials: "same-origin",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Cache": "no-cache"
            }
        };
        Axios.get("http://localhost:3000/api/isAuth", options)
        
        .then(response => { console.log(response.data);
        //    return response.data.auth
            authentication = response.data.auth 
             console.log(authentication);
        })
             
        .then(() => { return authentication; })
        

        return authentication;
        }
            
    // isAuthenticated() {
    //     return this.checkAuth()
    // }
    
}

export default new Auth();
