import React, { useState, useContext } from "react";
import { ApiContext } from "../../context/ApiContext";
import { UserContext } from "../../context/UserContext";

import { InputLabel, InputAdornment, Input, Button, Grid, Link } from '@material-ui/core';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import LabelIcon from '@material-ui/icons/Label';



export default function CreatePostForm(props) {
    const [tags, setTags] = useState([]);
    const [inputValue, setInputValue] = useState("");
    const [price, setPrice] = useState();
    const user = useContext(UserContext)
    const api = useContext(ApiContext)
    const { username, _id } = user.data

    const date = () => {

        const day = new Date().getDate()
        const month = new Date().getMonth() + 1
        const year = new Date().getFullYear()
        return day + "-" + month + "-" + year
    }

    const handleUpload = () => {

        props.images.forEach(image => {    
            const post = {
                creator: username,
                creatorId: _id,
                image: image,
                createdAt: date(),
                price: price,
                tags: tags
            }

            api.createPost(post)
        });
        console.log(new Date().getDate());
    }
    
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') { 
        setTags(prevTags => prevTags.concat(inputValue))    
        setInputValue("")   
        }
    }
    const handleTags = async (e) => {
        setTags(prevTags => prevTags.concat(inputValue))
        setInputValue("")
    }
    
    const tagsArray = tags.map(tag => {
        return (
            <Link href={tag} key={tag} onClick={(e) => e.preventDefault()} variant="body2">
                {" #" + tag}
            </Link>
        )
    })
    return (
        <div>
            <Grid item >
                <InputLabel style={{ fontSize: 13 }}>Choose a price</InputLabel>
                <Input
                    id="Price"
                    fullWidth
                    required
                    placeholder="5$"
                    type="number"
                    onChange={(e) => setPrice( e.target.value )}
                    startAdornment={ <InputAdornment position="start"> <MonetizationOnIcon /> </InputAdornment> }
                />
            </Grid>
            <br />
            <Grid item>
                <InputLabel style={{ fontSize: 13 }}>Tags</InputLabel>
                <Input
                    fullWidth
                    id="Tags"
                    value={inputValue}
                    placeholder="Red dress..."
                    onChange={(e) => setInputValue(e.target.value)}
                    onKeyDown={handleKeyDown}
                    startAdornment={ <InputAdornment position="start"> <LabelIcon /> </InputAdornment> }
                    endAdornment={ <Button onClick={handleTags} > ADD </Button> }
                />
            </Grid>
            <br />
            <Grid item>
                    {tagsArray && tagsArray}
            </Grid>
            <br />
            <Grid item>
                <Button
                    variant="contained"
                    color="default"
                    fullWidth
                    startIcon={<CloudUploadIcon />}
                    onClick={handleUpload}
                >
                    Upload
                </Button>
            </Grid>
        </div>
    )
}
