import React, { useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { UserContext } from "../../context/UserContext"
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardActions from "@material-ui/core/CardActions";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import EditProfileForm from "./EditProfileForm"
// import UploadImage from "./UplaodImage"
// import GetImage from "./GetImage"
import _ from "lodash"

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "90%",
        borderRadius: 20,
        margin: "2vh auto"
    },
    icons: {
        color: "#000",
        fontSize: "2vh",
    },
    title:{
        fontFamily: "Abril Fatface",
        color: "primary"
    },

}));

export default function Dashboard() {
    const classes = useStyles();
    const user = useContext(UserContext)  
    const {instagram, facebook, snapchat, twitter, flickr} = user.data  
    const accounts = [
        { instagram: instagram, icon: "fab fa-instagram"},
        { facebook: facebook, icon: "fab fa-facebook"},
        { snapchat: snapchat, icon: "fab fa-snapchat"},
        { twitter: twitter, icon: "fab fa-twitter"},
        { flickr: flickr, icon: "fab fa-flickr"}
]

    if (accounts) {
        accounts.map(account => {
            return (
                <Typography variant="body2">
                    <i class={account.icon}>&nbsp;</i>
                    <strong>{ _.upperFirst(account.account)}&nbsp;</strong>
                    {account.account}&nbsp;</Typography>
            )
            
        })
    }

    //     for (const [index, value] of Object.entries(accounts)) {
    //         accounts.push(
    //             <Typography variant="body2">
    //             <i class={value.account && value.icon}>&nbsp;</i>
    //             <strong>{value.account && _.upperFirst(index)}&nbsp;</strong>
    //             {value.account}&nbsp;</Typography>
    //         )
    //     }
    // }


    return (
        <div>

            {/* ----------------------------------   Header  ----------------------------------- */}

        <Card variant="outlined" className={classes.root}>
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardHeader
                        // avatar={
                        //     <UserAvatar variant="rounded" ></UserAvatar>
                        // }
                        title={<Typography className={classes.title} color="textPrimary" >
                        <h1>|{user.data.username}</h1>
                        </Typography>}
                            subheader={<p><i class="fas fa-at"></i> {user.data.email}  {user.data.webpage && user.data.webpage}</p>}
                        />
                </Box>
                    <Box mx={4} my={6}>
                        <EditProfileForm alert={alert} />
                    </Box>
            </Box>
        <Box style={{ width: '100%' }} >
            <Box display="flex" justifyContent="space-between">
                <Box mx={1} my={1}>
                    <CardActions >
                        {accounts && accounts}
                    </CardActions> 
                </Box>
            </Box>
        </Box>
        </Card>
            {/* ----------------------------------   Photos Section  ----------------------------------- */}
            {/* <GetImage /> */}
            {/* <UploadImage /> */}
        </div>
    );
}
